var relationshipDescription = '';

if (data.alternateContactRelationship === "attorneyOrLawyer"){
    relationshipDescription = "Attorney or lawyer";
}
else if (data.alternateContactRelationship === "fairHousingOrganization"){
    relationshipDescription = "Fair housing organization";
}
else if (data.alternateContactRelationship === "familyMemberOrFriend"){
    relationshipDescription = "Family member or friend";
}
else if (data.alternateContactRelationship === "other"){
    relationshipDescription = (!!data.alternativeContactOtherRelationshipDescription) ? ('Other: '+ data.alternativeContactOtherRelationshipDescription) : 'Other' ;
}

value = relationshipDescription;

